// src/router.js
import Vue from 'vue';
import Router from 'vue-router';
import HomeView from './views/HomeView.vue'; // Updated import for HomeView
import Category from './views/Category.vue';
import ImageDetail from './views/ImageDetail.vue';
import ShoppingCart from './views/ShoppingCart.vue';
import Checkout from './views/Checkout.vue';
import Profile from './views/Profile.vue';

Vue.use(Router);

export default new Router({
  routes: [
    { path: '/', component: HomeView }, // Updated component for the home route
    { path: '/category/:categoryName', component: Category },
    { path: '/image/:imageId', component: ImageDetail },
    { path: '/shopping-cart', component: ShoppingCart },
    { path: '/checkout', component: Checkout },
    { path: '/profile', component: Profile },
  ],
  mode: 'history',
});
