// src/router.js
import Vue from 'vue';
import Router from 'vue-router';
import HomeView from './views/HomeView.vue'; // Updated import for HomeView
import CategoryView from './views/CategoryView.vue';
import ImageDetailView from './views/ImageDetailView.vue';
import ShoppingCartView from './views/ShoppingCartView.vue';
import CheckoutView from './views/CheckoutView.vue';
import ProfileView from './views/ProfileView.vue';

Vue.use(Router);

export default new Router({
  routes: [
    { path: '/', component: HomeView }, // Updated component for the home route
    { path: '/category/:categoryName', component: CategoryView },
    { path: '/image/:imageId', component: ImageDetailView },
    { path: '/shopping-cart', component: ShoppingCartView },
    { path: '/checkout', component: CheckoutView },
    { path: '/profile', component: ProfileView },
  ],
  mode: 'history',
});
